// Parse incoming API payload with full context
const body = $input.item.json.body;

// Extract all required data
const projectId = body.projectId;
const userId = body.userId;
const s3Key = body.s3Key;
const projectBrief = body.projectBrief || {};
const summary = body.summary || {};
const reviewAndRefine = body.reviewAndRefine || [];

// Extract user answers from reviewAndRefine with validation
let targetAudience = '';
let coreMessage = '';
let businessGoal = '';

if (Array.isArray(reviewAndRefine)) {
  const targetAudQ = reviewAndRefine.find(q => q.id === 'targetAudience');
  const coreMessageQ = reviewAndRefine.find(q => q.id === 'coreMessage');
  const businessGoalQ = reviewAndRefine.find(q => q.id === 'businessGoal');

  targetAudience = targetAudQ?.userAnswer || '';
  coreMessage = coreMessageQ?.userAnswer || '';
  businessGoal = businessGoalQ?.userAnswer || '';
}

return {
  json: {
    projectId,
    userId,
    s3Key,
    projectBrief,
    summary,
    reviewAndRefine: {
      targetAudience,
      coreMessage,
      businessGoal
    },
    timestamp: new Date().toISOString()
  }
};

// Prepare comprehensive prompt for AI to generate 3-slide presentation (IMPROVED VERSION)
const data = $input.item.json;

// Build rich context from Stage 1 analysis
const slideSummaries = data.summary.slides
  .map(s => {
    let summary = `Slide ${s.index}: ${s.title}`;
    if (s.textContent) summary += `\nContent: ${s.textContent.join(', ')}`;
    if (s.bullets) summary += `\nBullets: ${s.bullets.join(', ')}`;
    if (s.visualDescription) summary += `\nVisuals: ${s.visualDescription}`;
    if (s.extractedNumbers) summary += `\nData: ${JSON.stringify(s.extractedNumbers)}`;
    if (s.category) summary += `\nCategory: ${s.category}`;
    return summary;
  })
  .join('\n\n');

// Extract metadata and narrative if available
const metadata = data.summary.presentationMetadata || {};
const narrative = data.summary.narrative || {};
const keyMetrics = data.summary.keyMetrics || {};

// Detect audience-specific tone
const audienceMap = {
  'Investors': 'confident, data-driven, emphasizing ROI and market opportunity',
  'Potential customers': 'benefit-focused, clear value proposition, problem-solution oriented',
  'Internal leadership': 'strategic, aligned with company goals, action-oriented',
  'Strategic partners': 'collaborative, mutual benefit, ecosystem value'
};

const tone = audienceMap[data.reviewAndRefine.targetAudience] || 'professional and clear';

const prompt = `You are an expert presentation designer specializing in creating compelling executive summaries for ${data.reviewAndRefine.targetAudience}.

**YOUR MISSION:**
Transform the full presentation into a powerful 3-slide executive summary that delivers maximum impact. Each slide must be visually striking and contain exactly ONE sentence that tells the story.

