// Parse AI response and format for PDF generation
const aiResponse = $input.item.json;
const projectId = $node['Parse API Payload'].json.projectId;

let slides;
try {
  const content = aiResponse.choices[0].message.content;
  // Extract JSON from response
  const jsonMatch = content.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    slides = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('No JSON found in AI response');
  }

  // Validate structure
  if (!slides.slide1 || !slides.slide2 || !slides.slide3) {
    throw new Error('Missing slides in AI response');
  }

  // Ensure titles are correct
  slides.slide1.title = 'THE PROBLEM';
  slides.slide2.title = 'THE SOLUTION';
  slides.slide3.title = 'THE ASK';

} catch (error) {
  // Fallback slides if parsing fails
  console.error('AI parsing error:', error.message);
  slides = {
    slide1: {
      title: 'THE PROBLEM',
      visual: 'Error generating content',
      sentence: 'Please retry the generation process.'
    },
    slide2: {
      title: 'THE SOLUTION',
      visual: 'Error generating content',
      sentence: 'Please retry the generation process.'
    },
    slide3: {
      title: 'THE ASK',
      visual: 'Error generating content',
      sentence: 'Please retry the generation process.'
    }
  };
}

return {
  json: {
    projectId,
    slide1: slides.slide1,
    slide2: slides.slide2,
    slide3: slides.slide3
  }
};

// Prepare S3 upload parameters
const projectId = $node['Parse API Payload'].json.projectId;
const userId = $node['Parse API Payload'].json.userId;

// S3 key format: outputs/{userId}/{projectId}/result.pdf
const s3Key = `outputs/${userId}/${projectId}/result.pdf`;
const s3Bucket = 'sliderx-outputs-dev';

return {
  json: {
    projectId,
    userId,
    s3Bucket,
    s3Key
  },
  binary: {
    data: $input.item.binary.data
  }
};

// Log error and format error response
const errorNode = $input.item.json;
const projectId = $node['Parse API Payload']?.json?.projectId || 'unknown';

const errorMessage = errorNode.error?.message || errorNode.message || 'Unknown error';
const errorStep = $node.name;

console.error(`Stage 2 Error at ${errorStep}:`, errorMessage);

return {
  json: {
    success: false,
    projectId,
    error: errorMessage,
    failedAt: errorStep,
    timestamp: new Date().toISOString()
  }
};
